import { nullable } from "@prismicio/types-internal/lib/validators/function"
import * as t from "io-ts"

export const RelatedDocument = t.exact(
	t.intersection([
		t.type({
			typ: t.string,
			id: t.string,
			language: t.string,
			groupLangId: t.string,
		}),
		t.partial({
			uid: nullable(t.string),
		}),
	]),
)

export type RelatedDocument = t.TypeOf<typeof RelatedDocument>
