import * as t from "io-ts"
import { mapOutput } from "io-ts-types/lib/mapOutput"

export const ColorDefType = "colorDef"
export const ColorDef = mapOutput(
	t.strict({
		color: t.string,
	}),
	(o) => ({ name: o.color, __TYPE__: ColorDefType } as const),
)
export type ColorDef = t.TypeOf<typeof ColorDef>
export type ColorDefO = t.OutputOf<typeof ColorDef>

export function fromColor(key: string): ColorDef {
	return { color: key }
}
