import * as t from "io-ts"
import { mapOutput } from "io-ts-types/lib/mapOutput"

export const DateDefType = "dateDef"
export const DateDef = mapOutput(
	t.strict({
		date: t.string,
	}),
	(o) => ({ name: o.date, __TYPE__: DateDefType } as const),
)
export type DateDef = t.TypeOf<typeof DateDef>
export type DateDefO = t.OutputOf<typeof DateDef>

export function fromDate(key: string): DateDef {
	return { date: key }
}
