import * as t from "io-ts"
import { mapOutput } from "io-ts-types/lib/mapOutput"

export const EmbedDefType = "embedDef"
export const EmbedDef = mapOutput(
	t.strict({
		embed: t.string,
	}),
	(o) => ({ name: o.embed, __TYPE__: EmbedDefType } as const),
)
export type EmbedDef = t.TypeOf<typeof EmbedDef>
export type EmbedDefO = t.OutputOf<typeof EmbedDef>

export function fromEmbed(key: string): EmbedDef {
	return { embed: key }
}
