import * as t from "io-ts"
import { mapOutput } from "io-ts-types/lib/mapOutput"

export const GeoPointDefType = "geopointDef"
export const GeoPointDef = mapOutput(
	t.strict({
		geopoint: t.string,
	}),
	(o) => ({ name: o.geopoint, __TYPE__: GeoPointDefType } as const),
)
export type GeoPointDef = t.TypeOf<typeof GeoPointDef>
export type GeoPointDefO = t.OutputOf<typeof GeoPointDef>

export function fromGeoPoint(key: string): GeoPointDef {
	return { geopoint: key }
}
