import * as t from "io-ts"
import { mapOutput } from "io-ts-types/lib/mapOutput"

export const IntegrationFieldDefType = "integrationFieldDef"
export const IntegrationFieldDef = mapOutput(
	t.strict({
		integrationFields: t.string,
	}),
	(o) => ({ name: o.integrationFields, __TYPE__: IntegrationFieldDefType } as const),
)
export type IntegrationFieldDef = t.TypeOf<typeof IntegrationFieldDef>
export type IntegrationFieldDefO = t.OutputOf<typeof IntegrationFieldDef>

export function fromIntegrationField(key: string): IntegrationFieldDef {
	return { integrationFields: key }
}
