import * as t from "io-ts"
import { mapOutput } from "io-ts-types/lib/mapOutput"

export const NumberDefType = "numberDef"
export const NumberDef = mapOutput(
	t.strict({
		number: t.string,
	}),
	(o) => ({ name: o.number, __TYPE__: NumberDefType } as const),
)
export type NumberDef = t.TypeOf<typeof NumberDef>
export type NumberDefO = t.OutputOf<typeof NumberDef>

export function fromNumber(key: string): NumberDef {
	return { number: key }
}
