import * as t from "io-ts"
import { mapOutput } from "io-ts-types/lib/mapOutput"

export const RangeDefType = "RangeDef"
export const RangeDef = mapOutput(
	t.strict({
		range: t.string,
	}),
	(o) => ({ name: o.range, __TYPE__: RangeDefType } as const),
)
export type RangeDef = t.TypeOf<typeof RangeDef>
export type RangeDefO = t.OutputOf<typeof RangeDef>

export function fromRange(key: string): RangeDef {
	return { range: key }
}
