import * as t from "io-ts"
import { mapOutput } from "io-ts-types/lib/mapOutput"

export const SeparatorDefType = "separatorDef"
export const SeparatorDef = mapOutput(
	t.strict({
		separator: t.string,
	}),
	(o) => ({ name: o.separator, __TYPE__: SeparatorDefType } as const),
)
export type SeparatorDef = t.TypeOf<typeof SeparatorDef>
export type SeparatorDefO = t.OutputOf<typeof SeparatorDef>

export function fromSeparator(key: string): SeparatorDef {
	return { separator: key }
}
