import * as t from "io-ts"
import { mapOutput } from "io-ts-types/lib/mapOutput"

export const StructuredTextDefType = "structuredTextDef"
export const StructuredTextDef = mapOutput(
	t.strict({
		structuredtext: t.string,
	}),
	(o) => ({ name: o.structuredtext, __TYPE__: StructuredTextDefType } as const),
)
export type StructuredTextDef = t.TypeOf<typeof StructuredTextDef>
export type StructuredTextDefO = t.OutputOf<typeof StructuredTextDef>

export function fromRichText(key: string): StructuredTextDef {
	return { structuredtext: key }
}
