import * as t from "io-ts"
import { mapOutput } from "io-ts-types/lib/mapOutput"

export const TextDefType = "textDef"
export const TextDef = mapOutput(
	t.strict({
		text: t.string,
	}),
	(o) => ({ name: o.text, __TYPE__: TextDefType } as const),
)
export type TextDef = t.TypeOf<typeof TextDef>
export type TextDefO = t.OutputOf<typeof TextDef>

export function fromText(key: string): TextDef {
	return { text: key }
}
