import * as t from "io-ts"
import { mapOutput } from "io-ts-types/lib/mapOutput"

export const TimestampDefType = "timestampDef"
export const TimestampDef = mapOutput(
	t.strict({
		timestamp: t.string,
	}),
	(o) => ({ name: o.timestamp, __TYPE__: TimestampDefType } as const),
)
export type TimestampDef = t.TypeOf<typeof TimestampDef>
export type TimestampDefO = t.OutputOf<typeof TimestampDef>

export function fromTimestamp(key: string): TimestampDef {
	return { timestamp: key }
}
