import * as t from "io-ts"
import { mapOutput } from "io-ts-types/lib/mapOutput"

export const UIDDefType = "UIDDef"
export const UIDDef = mapOutput(
	t.strict({
		uid: t.string,
	}),
	(o) => ({ name: o.uid, __TYPE__: UIDDefType } as const),
)
export type UIDDef = t.TypeOf<typeof UIDDef>
export type UIDDefO = t.OutputOf<typeof UIDDef>

export function fromUID(key: string): UIDDef {
	return { uid: key }
}
