import { UIDContent } from "@prismicio/types-internal/lib/documents/widgets"

import type { Renderer, UIDDefO } from "../models"

const UIDRenderer: Renderer<UIDDefO, UIDContent> = {
	renderV1(uid: UIDContent): unknown {
		return { value: UIDContent.encode(uid) }
	},

	renderV2(_def: UIDDefO, uid: UIDContent): unknown {
		return UIDContent.encode(uid)
	},

	renderDefault(_: UIDDefO): unknown {
		return null
	},

	renderMocks(_def: UIDDefO, uid: UIDContent): unknown {
		return UIDContent.encode(uid)
	},
}

export default UIDRenderer
