import type { BooleanContent } from "@prismicio/types-internal/lib/documents/widgets/nestable"

import type { BooleanFieldDefO, Renderer } from "../../models"

const BooleanRenderer: Renderer<BooleanFieldDefO, BooleanContent> = {
	renderV1(content: BooleanContent): unknown {
		return {
			type: "Boolean",
			value: content.value,
		}
	},

	renderV2(_def: BooleanFieldDefO, content: BooleanContent): unknown {
		return content.value
	},

	renderDefault(_def: BooleanFieldDefO): unknown {
		return null
	},

	renderMocks(def: BooleanFieldDefO, content: BooleanContent): unknown {
		return this.renderV2(def, content)
	},
}

export default BooleanRenderer
