import type { EmbedContentO } from "@prismicio/types-internal/lib/documents/widgets/nestable"

import type { EmbedDefO, Renderer } from "../../models"

const EmbedRenderer: Renderer<EmbedDefO, EmbedContentO> = {
	renderV1(content: EmbedContentO): unknown {
		return {
			type: "Embed",
			value: {
				oembed: content.all,
			},
		}
	},

	renderV2(_def: EmbedDefO, content: EmbedContentO): unknown {
		return content.all
	},

	renderDefault(_def: EmbedDefO): unknown {
		return {}
	},

	renderMocks(def: EmbedDefO, content: EmbedContentO): unknown {
		return this.renderV2(def, content)
	},
}

export default EmbedRenderer
