import type { GeoPointContentO } from "@prismicio/types-internal/lib/documents/widgets/nestable"

import type { GeoPointDefO, Renderer } from "../../models"

const GeoPointRenderer: Renderer<GeoPointDefO, GeoPointContentO> = {
	renderV1(content: GeoPointContentO): unknown {
		return {
			type: "GeoPoint",
			value: {
				latitude: content.position.lat,
				longitude: content.position.lng,
			},
		}
	},

	renderV2(_def: GeoPointDefO, content: GeoPointContentO): unknown {
		return {
			latitude: content.position.lat,
			longitude: content.position.lng,
		}
	},

	renderDefault(_def: GeoPointDefO): unknown {
		return {}
	},

	renderMocks(def: GeoPointDefO, content: GeoPointContentO): unknown {
		return this.renderV2(def, content)
	},
}

export default GeoPointRenderer
