import type { FileLink } from "@prismicio/types-internal/lib/documents/widgets/nestable/Link"

import type { RenderContext } from "../../../models"
import type { AnyLinkDef, LinkRenderer, MediaLinkDef } from "../../../models"

const FileLinkRenderer: (ctx: RenderContext) => LinkRenderer<MediaLinkDef, FileLink> = (ctx) => ({
	renderV1(link: FileLink): unknown {
		return {
			type: "Link.file",
			value: {
				file: {
					name: link.name,
					kind: link.kind,
					url: ctx.urlRewriter.rewriteFileUrl(link.url),
					size: link.size,
				},
			},
		}
	},

	renderV2(_def: MediaLinkDef | AnyLinkDef, link: FileLink): unknown {
		return {
			link_type: "Media",
			name: link.name,
			kind: link.kind,
			url: ctx.urlRewriter.rewriteFileUrl(link.url),
			size: link.size,
		}
	},

	renderMocks(_def: MediaLinkDef | AnyLinkDef, _link: FileLink): unknown {
		return {
			link_type: "Media",
			name: "mock",
			kind: "image",
			url: "https://source.unsplash.com/random",
			size: "10kB",
		}
	},
})

export default FileLinkRenderer
