import type { SeparatorContent } from "@prismicio/types-internal/lib/documents/widgets/nestable"

import type { Renderer, SeparatorDefO } from "../../models"

const SeparatorRenderer: Renderer<SeparatorDefO, SeparatorContent> = {
	renderV1(_content: SeparatorContent): unknown {
		return {
			type: "Separator",
		}
	},

	renderV2(_def: SeparatorDefO, _content: SeparatorContent): unknown {
		return true
	},

	renderDefault(_def: SeparatorDefO): unknown {
		return true
	},

	renderMocks(def: SeparatorDefO, content: SeparatorContent): unknown {
		return this.renderV2(def, content)
	},
}

export default SeparatorRenderer
